var path = '/var/tmp/Roost/RoostGPT/bhavyaPostmanTest/3751e9fe-0a37-47de-8145-dbdd831b4571/roost_user_input.txt';

fs.readFile(path, 'utf8', function(err, userInput){
    if(err){
        console.log('Could not read file: ' + path);
        return;
    }
    // Parse user input into JSON
    var testInput = JSON.parse(userInput);

    // Make a POST request with the payload from roost_user_input.txt
    pm.sendRequest({
        url: `${pm.environment.get('url')}/test`,
        method: 'POST',
        header: 'Content-Type:application/json',
        body: {
            mode: 'raw',
            raw: JSON.stringify(testInput)
        }
    }, function(err, response){
        if(err){
            console.log('Could not send request: ' + err);
            return;
        }

        // Extract response body
        var responseBody = response.json();

        // Add your tests here
        pm.test('Status code is 200', function(){
            pm.response.to.equal(200);
        });

        pm.test('Response body has property: nobelPrizes', function(){
            pm.expect(responseBody).to.have.property('nobelPrizes');
        });

        // Iterate through each object in nobelPrizes array
        responseBody.nobelPrizes.forEach(function(nobelPrize, index){
            pm.test(`nobelPrize at index ${index} has required fields`, function(){
                pm.expect(nobelPrize).to.have.property('awardYear');
                pm.expect(nobelPrize).to.have.property('category');
                pm.expect(nobelPrize).to.have.property('categoryFullName');
                pm.expect(nobelPrize).to.have.property('dateAwarded');
                pm.expect(nobelPrize).to.have.property('prizeAmount');
                pm.expect(nobelPrize).to.have.property('prizeAmountAdjusted');
                pm.expect(nobelPrize).to.have.property('topMotivation');
                pm.expect(nobelPrize).to.have.property('laureates');
            });
        });

        // Test for other properties in the response
        // metadata, links etc. 

    });
});
